INSERT INTO `app_less` VALUES (9,'test-main','.btn-trans{\r\n    background-color: transparent;\r\n    border: 1px solid;\r\n}'),(10,'simplex-variables','// Simplex 3.3.6\r\n// Variables\r\n// --------------------------------------------------\r\n\r\n\r\n//== Colors\r\n//\r\n//## Gray and brand colors for use across Bootstrap.\r\n\r\n@gray-base:              #000;\r\n@gray-darker:            lighten(@gray-base, 13.5%); // #222\r\n@gray-dark:              #444;\r\n@gray:                   #777;\r\n@gray-light:             #808080;\r\n@gray-lighter:           #ddd;\r\n\r\n@brand-primary:         #D9230F;\r\n@brand-success:         #469408;\r\n@brand-info:            #029ACF;\r\n@brand-warning:         #9B479F;\r\n@brand-danger:          #D9831F;\r\n\r\n\r\n//== Scaffolding\r\n//\r\n//## Settings for some of the most global styles.\r\n\r\n//** Background color for `<body>`.\r\n@body-bg:               #FCFCFC;\r\n//** Global text color on `<body>`.\r\n@text-color:            @gray;\r\n\r\n//** Global textual link color.\r\n@link-color:            @brand-primary;\r\n//** Link hover color set via `darken()` function.\r\n@link-hover-color:      darken(@link-color, 15%);\r\n//** Link hover decoration.\r\n@link-hover-decoration: underline;\r\n\r\n\r\n//== Typography\r\n//\r\n//## Font, line-height, and color for body text, headings, and more.\r\n\r\n@font-family-sans-serif:  \"Open Sans\", \"Helvetica Neue\", Helvetica, Arial, sans-serif;\r\n@font-family-serif:       Georgia, \"Times New Roman\", Times, serif;\r\n//** Default monospace fonts for `<code>`, `<kbd>`, and `<pre>`.\r\n@font-family-monospace:   Menlo, Monaco, Consolas, \"Courier New\", monospace;\r\n@font-family-base:        @font-family-sans-serif;\r\n\r\n@font-size-base:          13px;\r\n@font-size-large:         ceil((@font-size-base * 1.25)); // ~18px\r\n@font-size-small:         ceil((@font-size-base * 0.85)); // ~12px\r\n\r\n@font-size-h1:            floor((@font-size-base * 2.6)); // ~36px\r\n@font-size-h2:            floor((@font-size-base * 2.15)); // ~30px\r\n@font-size-h3:            ceil((@font-size-base * 1.7)); // ~24px\r\n@font-size-h4:            ceil((@font-size-base * 1.25)); // ~18px\r\n@font-size-h5:            @font-size-base;\r\n@font-size-h6:            ceil((@font-size-base * 0.85)); // ~12px\r\n\r\n//** Unit-less `line-height` for use in components like buttons.\r\n@line-height-base:        1.428571429; // 20/14\r\n//** Computed \"line-height\" (`font-size` * `line-height`) for use with `margin`, `padding`, etc.\r\n@line-height-computed:    floor((@font-size-base * @line-height-base)); // ~20px\r\n\r\n//** By default, this inherits from the `<body>`.\r\n@headings-font-family:    @font-family-sans-serif;\r\n@headings-font-weight:    300;\r\n@headings-line-height:    1.1;\r\n@headings-color:          @gray-dark;\r\n\r\n\r\n//== Iconography\r\n//\r\n//## Specify custom location and filename of the included Glyphicons icon font. Useful for those including Bootstrap via Bower.\r\n\r\n//** Load fonts from this directory.\r\n@icon-font-path:          \"../fonts/\";\r\n//** File name for all font files.\r\n@icon-font-name:          \"glyphicons-halflings-regular\";\r\n//** Element ID within SVG icon file.\r\n@icon-font-svg-id:        \"glyphicons_halflingsregular\";\r\n\r\n\r\n//== Components\r\n//\r\n//## Define common padding and border radius sizes and more. Values based on 14px text and 1.428 line-height (~20px to start).\r\n\r\n@padding-base-vertical:     8px;\r\n@padding-base-horizontal:   12px;\r\n\r\n@padding-large-vertical:    14px;\r\n@padding-large-horizontal:  16px;\r\n\r\n@padding-small-vertical:    5px;\r\n@padding-small-horizontal:  10px;\r\n\r\n@padding-xs-vertical:       1px;\r\n@padding-xs-horizontal:     5px;\r\n\r\n@line-height-large:         1.3333333; // extra decimals for Win 8.1 Chrome\r\n@line-height-small:         1.5;\r\n\r\n@border-radius-base:        4px;\r\n@border-radius-large:       6px;\r\n@border-radius-small:       3px;\r\n\r\n//** Global color for active items (e.g., navs or dropdowns).\r\n@component-active-color:    #fff;\r\n//** Global background color for active items (e.g., navs or dropdowns).\r\n@component-active-bg:       @brand-primary;\r\n\r\n//** Width of the `border` for generating carets that indicator dropdowns.\r\n@caret-width-base:          4px;\r\n//** Carets increase slightly in size for larger components.\r\n@caret-width-large:         5px;\r\n\r\n\r\n//== Tables\r\n//\r\n//## Customizes the `.table` component with basic values, each used across all table variations.\r\n\r\n//** Padding for `<th>`s and `<td>`s.\r\n@table-cell-padding:            8px;\r\n//** Padding for cells in `.table-condensed`.\r\n@table-condensed-cell-padding:  5px;\r\n\r\n//** Default background color used for all tables.\r\n@table-bg:                      transparent;\r\n//** Background color used for `.table-striped`.\r\n@table-bg-accent:               #f9f9f9;\r\n//** Background color used for `.table-hover`.\r\n@table-bg-hover:                #f5f5f5;\r\n@table-bg-active:               @table-bg-hover;\r\n\r\n//** Border color for table and cell borders.\r\n@table-border-color:            #ddd;\r\n\r\n\r\n//== Buttons\r\n//\r\n//## For each of Bootstrap\'s buttons, define text, background and border color.\r\n\r\n@btn-font-weight:                normal;\r\n\r\n@btn-default-color:              #fff;\r\n@btn-default-bg:                 #474949;\r\n@btn-default-border:             @btn-default-bg;\r\n\r\n@btn-primary-color:              @btn-default-color;\r\n@btn-primary-bg:                 @brand-primary;\r\n@btn-primary-border:             @btn-primary-bg;\r\n\r\n@btn-success-color:              @btn-default-color;\r\n@btn-success-bg:                 @brand-success;\r\n@btn-success-border:             @btn-success-bg;\r\n\r\n@btn-info-color:                 @btn-default-color;\r\n@btn-info-bg:                    @brand-info;\r\n@btn-info-border:                @btn-info-bg;\r\n\r\n@btn-warning-color:              @btn-default-color;\r\n@btn-warning-bg:                 @brand-warning;\r\n@btn-warning-border:             @btn-warning-bg;\r\n\r\n@btn-danger-color:               @btn-default-color;\r\n@btn-danger-bg:                  @brand-danger;\r\n@btn-danger-border:              @btn-danger-bg;\r\n\r\n@btn-link-disabled-color:        @gray-light;\r\n\r\n// Allows for customizing button radius independently from global border radius\r\n@btn-border-radius-base:         @border-radius-base;\r\n@btn-border-radius-large:        @border-radius-large;\r\n@btn-border-radius-small:        @border-radius-small;\r\n\r\n\r\n//== Forms\r\n//\r\n//##\r\n\r\n//** `<input>` background color\r\n@input-bg:                       #fff;\r\n//** `<input disabled>` background color\r\n@input-bg-disabled:              @gray-lighter;\r\n\r\n//** Text color for `<input>`s\r\n@input-color:                    @text-color;\r\n//** `<input>` border color\r\n@input-border:                   #ddd;\r\n\r\n// TODO: Rename `@input-border-radius` to `@input-border-radius-base` in v4\r\n//** Default `.form-control` border radius\r\n// This has no effect on `<select>`s in some browsers, due to the limited stylability of `<select>`s in CSS.\r\n@input-border-radius:            @border-radius-base;\r\n//** Large `.form-control` border radius\r\n@input-border-radius-large:      @border-radius-large;\r\n//** Small `.form-control` border radius\r\n@input-border-radius-small:      @border-radius-small;\r\n\r\n//** Border color for inputs on focus\r\n@input-border-focus:             #66afe9;\r\n\r\n//** Placeholder text color\r\n@input-color-placeholder:        @gray-lighter;\r\n\r\n//** Default `.form-control` height\r\n@input-height-base:              (@line-height-computed + (@padding-base-vertical * 2) + 2);\r\n//** Large `.form-control` height\r\n@input-height-large:             (ceil(@font-size-large * @line-height-large) + (@padding-large-vertical * 2) + 2);\r\n//** Small `.form-control` height\r\n@input-height-small:             (floor(@font-size-small * @line-height-small) + (@padding-small-vertical * 2) + 2);\r\n\r\n//** `.form-group` margin\r\n@form-group-margin-bottom:       15px;\r\n\r\n@legend-color:                   @text-color;\r\n@legend-border-color:            #e5e5e5;\r\n\r\n//** Background color for textual input addons\r\n@input-group-addon-bg:           @gray-lighter;\r\n//** Border color for textual input addons\r\n@input-group-addon-border-color: @input-border;\r\n\r\n//** Disabled cursor for form controls and buttons.\r\n@cursor-disabled:                not-allowed;\r\n\r\n\r\n//== Dropdowns\r\n//\r\n//## Dropdown menu container and contents.\r\n\r\n//** Background for the dropdown menu.\r\n@dropdown-bg:                    #fff;\r\n//** Dropdown menu `border-color`.\r\n@dropdown-border:                rgba(0,0,0,.15);\r\n//** Dropdown menu `border-color` **for IE8**.\r\n@dropdown-fallback-border:       #ccc;\r\n//** Divider color for between dropdown items.\r\n@dropdown-divider-bg:            #e5e5e5;\r\n\r\n//** Dropdown link text color.\r\n@dropdown-link-color:            @gray-dark;\r\n//** Hover color for dropdown links.\r\n@dropdown-link-hover-color:      #fff;\r\n//** Hover background for dropdown links.\r\n@dropdown-link-hover-bg:         @component-active-bg;\r\n\r\n//** Active dropdown menu item text color.\r\n@dropdown-link-active-color:     #fff;\r\n//** Active dropdown menu item background color.\r\n@dropdown-link-active-bg:        @component-active-bg;\r\n\r\n//** Disabled dropdown menu item background color.\r\n@dropdown-link-disabled-color:   @gray-light;\r\n\r\n//** Text color for headers within dropdown menus.\r\n@dropdown-header-color:          @gray-light;\r\n\r\n//** Deprecated `@dropdown-caret-color` as of v3.1.0\r\n@dropdown-caret-color:           #000;\r\n\r\n\r\n//-- Z-index master list\r\n//\r\n// Warning: Avoid customizing these values. They\'re used for a bird\'s eye view\r\n// of components dependent on the z-axis and are designed to all work together.\r\n//\r\n// Note: These variables are not generated into the Customizer.\r\n\r\n@zindex-navbar:            1000;\r\n@zindex-dropdown:          1000;\r\n@zindex-popover:           1060;\r\n@zindex-tooltip:           1070;\r\n@zindex-navbar-fixed:      1030;\r\n@zindex-modal-background:  1040;\r\n@zindex-modal:             1050;\r\n\r\n\r\n//== Media queries breakpoints\r\n//\r\n//## Define the breakpoints at which your layout will change, adapting to different screen sizes.\r\n\r\n// Extra small screen / phone\r\n//** Deprecated `@screen-xs` as of v3.0.1\r\n@screen-xs:                  480px;\r\n//** Deprecated `@screen-xs-min` as of v3.2.0\r\n@screen-xs-min:              @screen-xs;\r\n//** Deprecated `@screen-phone` as of v3.0.1\r\n@screen-phone:               @screen-xs-min;\r\n\r\n// Small screen / tablet\r\n//** Deprecated `@screen-sm` as of v3.0.1\r\n@screen-sm:                  768px;\r\n@screen-sm-min:              @screen-sm;\r\n//** Deprecated `@screen-tablet` as of v3.0.1\r\n@screen-tablet:              @screen-sm-min;\r\n\r\n// Medium screen / desktop\r\n//** Deprecated `@screen-md` as of v3.0.1\r\n@screen-md:                  992px;\r\n@screen-md-min:              @screen-md;\r\n//** Deprecated `@screen-desktop` as of v3.0.1\r\n@screen-desktop:             @screen-md-min;\r\n\r\n// Large screen / wide desktop\r\n//** Deprecated `@screen-lg` as of v3.0.1\r\n@screen-lg:                  1200px;\r\n@screen-lg-min:              @screen-lg;\r\n//** Deprecated `@screen-lg-desktop` as of v3.0.1\r\n@screen-lg-desktop:          @screen-lg-min;\r\n\r\n// So media queries don\'t overlap when required, provide a maximum\r\n@screen-xs-max:              (@screen-sm-min - 1);\r\n@screen-sm-max:              (@screen-md-min - 1);\r\n@screen-md-max:              (@screen-lg-min - 1);\r\n\r\n\r\n//== Grid system\r\n//\r\n//## Define your custom responsive grid.\r\n\r\n//** Number of columns in the grid.\r\n@grid-columns:              12;\r\n//** Padding between columns. Gets divided in half for the left and right.\r\n@grid-gutter-width:         30px;\r\n// Navbar collapse\r\n//** Point at which the navbar becomes uncollapsed.\r\n@grid-float-breakpoint:     @screen-sm-min;\r\n//** Point at which the navbar begins collapsing.\r\n@grid-float-breakpoint-max: (@grid-float-breakpoint - 1);\r\n\r\n\r\n//== Container sizes\r\n//\r\n//## Define the maximum width of `.container` for different screen sizes.\r\n\r\n// Small screen / tablet\r\n@container-tablet:             (720px + @grid-gutter-width);\r\n//** For `@screen-sm-min` and up.\r\n@container-sm:                 @container-tablet;\r\n\r\n// Medium screen / desktop\r\n@container-desktop:            (940px + @grid-gutter-width);\r\n//** For `@screen-md-min` and up.\r\n@container-md:                 @container-desktop;\r\n\r\n// Large screen / wide desktop\r\n@container-large-desktop:      (1140px + @grid-gutter-width);\r\n//** For `@screen-lg-min` and up.\r\n@container-lg:                 @container-large-desktop;\r\n\r\n\r\n//== Navbar\r\n//\r\n//##\r\n\r\n// Basics of a navbar\r\n@navbar-height:                    40px;\r\n@navbar-margin-bottom:             @line-height-computed;\r\n@navbar-border-radius:             @border-radius-base;\r\n@navbar-padding-horizontal:        floor((@grid-gutter-width / 2));\r\n@navbar-padding-vertical:          ((@navbar-height - @line-height-computed) / 2);\r\n@navbar-collapse-max-height:       340px;\r\n\r\n@navbar-default-color:             @gray;\r\n@navbar-default-bg:                #fff;\r\n@navbar-default-border:            darken(@navbar-default-bg, 6.5%);\r\n\r\n// Navbar links\r\n@navbar-default-link-color:                @gray;\r\n@navbar-default-link-hover-color:          @brand-primary;\r\n@navbar-default-link-hover-bg:             transparent;\r\n@navbar-default-link-active-color:         @navbar-default-link-hover-color;\r\n@navbar-default-link-active-bg:            transparent;\r\n@navbar-default-link-disabled-color:       #444;\r\n@navbar-default-link-disabled-bg:          transparent;\r\n\r\n// Navbar brand label\r\n@navbar-default-brand-color:               @navbar-default-link-color;\r\n@navbar-default-brand-hover-color:         @navbar-default-link-hover-color;\r\n@navbar-default-brand-hover-bg:            transparent;\r\n\r\n// Navbar toggle\r\n@navbar-default-toggle-hover-bg:           #ddd;\r\n@navbar-default-toggle-icon-bar-bg:        #ccc;\r\n@navbar-default-toggle-border-color:       #ddd;\r\n\r\n\r\n//=== Inverted navbar\r\n// Reset inverted navbar basics\r\n@navbar-inverse-color:                      lighten(@brand-primary, 40%);\r\n@navbar-inverse-bg:                         @brand-primary;\r\n@navbar-inverse-border:                     darken(@navbar-inverse-bg, 10%);\r\n\r\n// Inverted navbar links\r\n@navbar-inverse-link-color:                 lighten(@brand-primary, 40%);\r\n@navbar-inverse-link-hover-color:           #fff;\r\n@navbar-inverse-link-hover-bg:              transparent;\r\n@navbar-inverse-link-active-color:          @navbar-inverse-link-hover-color;\r\n@navbar-inverse-link-active-bg:             transparent;\r\n@navbar-inverse-link-disabled-color:        #ccc;\r\n@navbar-inverse-link-disabled-bg:           transparent;\r\n\r\n// Inverted navbar brand label\r\n@navbar-inverse-brand-color:                @navbar-inverse-link-color;\r\n@navbar-inverse-brand-hover-color:          #fff;\r\n@navbar-inverse-brand-hover-bg:             transparent;\r\n\r\n// Inverted navbar toggle\r\n@navbar-inverse-toggle-hover-bg:            darken(@navbar-inverse-bg, 10%);\r\n@navbar-inverse-toggle-icon-bar-bg:         #fff;\r\n@navbar-inverse-toggle-border-color:        darken(@navbar-inverse-bg, 10%);\r\n\r\n\r\n//== Navs\r\n//\r\n//##\r\n\r\n//=== Shared nav styles\r\n@nav-link-padding:                          10px 15px;\r\n@nav-link-hover-bg:                         @gray-lighter;\r\n\r\n@nav-disabled-link-color:                   @gray-light;\r\n@nav-disabled-link-hover-color:             @gray-light;\r\n\r\n//== Tabs\r\n@nav-tabs-border-color:                     #ddd;\r\n\r\n@nav-tabs-link-hover-border-color:          @gray-lighter;\r\n\r\n@nav-tabs-active-link-hover-bg:             @body-bg;\r\n@nav-tabs-active-link-hover-color:          @gray;\r\n@nav-tabs-active-link-hover-border-color:   #ddd;\r\n\r\n@nav-tabs-justified-link-border-color:            #ddd;\r\n@nav-tabs-justified-active-link-border-color:     @body-bg;\r\n\r\n//== Pills\r\n@nav-pills-border-radius:                   @border-radius-base;\r\n@nav-pills-active-link-hover-bg:            @component-active-bg;\r\n@nav-pills-active-link-hover-color:         @component-active-color;\r\n\r\n\r\n//== Pagination\r\n//\r\n//##\r\n\r\n@pagination-color:                     @headings-color;\r\n@pagination-bg:                        #fff;\r\n@pagination-border:                    #ddd;\r\n\r\n@pagination-hover-color:               #fff;\r\n@pagination-hover-bg:                  @brand-primary;\r\n@pagination-hover-border:              @brand-primary;\r\n\r\n@pagination-active-color:              #fff;\r\n@pagination-active-bg:                 @brand-primary;\r\n@pagination-active-border:             @brand-primary;\r\n\r\n@pagination-disabled-color:            @gray-lighter;\r\n@pagination-disabled-bg:               #fff;\r\n@pagination-disabled-border:           #ddd;\r\n\r\n\r\n//== Pager\r\n//\r\n//##\r\n\r\n@pager-bg:                             @pagination-bg;\r\n@pager-border:                         @pagination-border;\r\n@pager-border-radius:                  15px;\r\n\r\n@pager-hover-bg:                       @brand-primary;\r\n\r\n@pager-active-bg:                      @brand-primary;\r\n@pager-active-color:                   #fff;\r\n\r\n@pager-disabled-color:                 @gray-lighter;\r\n\r\n\r\n//== Jumbotron\r\n//\r\n//##\r\n\r\n@jumbotron-padding:              30px;\r\n@jumbotron-color:                inherit;\r\n@jumbotron-bg:                   darken(@body-bg, 3%);\r\n@jumbotron-heading-color:        inherit;\r\n@jumbotron-font-size:            ceil((@font-size-base * 1.5));\r\n@jumbotron-heading-font-size:    ceil((@font-size-base * 4.5));\r\n\r\n\r\n//== Form states and alerts\r\n//\r\n//## Define colors for form feedback states and, by default, alerts.\r\n\r\n@state-success-text:             #468847;\r\n@state-success-bg:               #dff0d8;\r\n@state-success-border:           darken(spin(@state-success-bg, -10), 5%);\r\n\r\n@state-info-text:                #3a87ad;\r\n@state-info-bg:                  #d9edf7;\r\n@state-info-border:              darken(spin(@state-info-bg, -10), 7%);\r\n\r\n@state-warning-text:             #c09853;\r\n@state-warning-bg:               #fcf8e3;\r\n@state-warning-border:           darken(spin(@state-warning-bg, -10), 3%);\r\n\r\n@state-danger-text:              #b94a48;\r\n@state-danger-bg:                #f2dede;\r\n@state-danger-border:            darken(spin(@state-danger-bg, -10), 3%);\r\n\r\n\r\n//== Tooltips\r\n//\r\n//##\r\n\r\n//** Tooltip max width\r\n@tooltip-max-width:           200px;\r\n//** Tooltip text color\r\n@tooltip-color:               #fff;\r\n//** Tooltip background color\r\n@tooltip-bg:                  #000;\r\n@tooltip-opacity:             .9;\r\n\r\n//** Tooltip arrow width\r\n@tooltip-arrow-width:         5px;\r\n//** Tooltip arrow color\r\n@tooltip-arrow-color:         @tooltip-bg;\r\n\r\n\r\n//== Popovers\r\n//\r\n//##\r\n\r\n//** Popover body background color\r\n@popover-bg:                          #fff;\r\n//** Popover maximum width\r\n@popover-max-width:                   276px;\r\n//** Popover border color\r\n@popover-border-color:                rgba(0,0,0,.2);\r\n//** Popover fallback border color\r\n@popover-fallback-border-color:       #ccc;\r\n\r\n//** Popover title background color\r\n@popover-title-bg:                    darken(@popover-bg, 3%);\r\n\r\n//** Popover arrow width\r\n@popover-arrow-width:                 10px;\r\n//** Popover arrow color\r\n@popover-arrow-color:                 @popover-bg;\r\n\r\n//** Popover outer arrow width\r\n@popover-arrow-outer-width:           (@popover-arrow-width + 1);\r\n//** Popover outer arrow color\r\n@popover-arrow-outer-color:           fadein(@popover-border-color, 5%);\r\n//** Popover outer arrow fallback color\r\n@popover-arrow-outer-fallback-color:  darken(@popover-fallback-border-color, 20%);\r\n\r\n\r\n//== Labels\r\n//\r\n//##\r\n\r\n//** Default label background color\r\n@label-default-bg:            @btn-default-bg;\r\n//** Primary label background color\r\n@label-primary-bg:            @brand-primary;\r\n//** Success label background color\r\n@label-success-bg:            @brand-success;\r\n//** Info label background color\r\n@label-info-bg:               @brand-info;\r\n//** Warning label background color\r\n@label-warning-bg:            @brand-warning;\r\n//** Danger label background color\r\n@label-danger-bg:             @brand-danger;\r\n\r\n//** Default label text color\r\n@label-color:                 #fff;\r\n//** Default text color of a linked label\r\n@label-link-hover-color:      #fff;\r\n\r\n\r\n//== Modals\r\n//\r\n//##\r\n\r\n//** Padding applied to the modal body\r\n@modal-inner-padding:         20px;\r\n\r\n//** Padding applied to the modal title\r\n@modal-title-padding:         15px;\r\n//** Modal title line-height\r\n@modal-title-line-height:     @line-height-base;\r\n\r\n//** Background color of modal content area\r\n@modal-content-bg:                             #fff;\r\n//** Modal content border color\r\n@modal-content-border-color:                   rgba(0,0,0,.2);\r\n//** Modal content border color **for IE8**\r\n@modal-content-fallback-border-color:          #999;\r\n\r\n//** Modal backdrop background color\r\n@modal-backdrop-bg:           #000;\r\n//** Modal backdrop opacity\r\n@modal-backdrop-opacity:      .5;\r\n//** Modal header border color\r\n@modal-header-border-color:   #e5e5e5;\r\n//** Modal footer border color\r\n@modal-footer-border-color:   @modal-header-border-color;\r\n\r\n@modal-lg:                    900px;\r\n@modal-md:                    600px;\r\n@modal-sm:                    300px;\r\n\r\n\r\n//== Alerts\r\n//\r\n//## Define alert colors, border radius, and padding.\r\n\r\n@alert-padding:               15px;\r\n@alert-border-radius:         @border-radius-base;\r\n@alert-link-font-weight:      bold;\r\n\r\n@alert-success-bg:            @state-success-bg;\r\n@alert-success-text:          @state-success-text;\r\n@alert-success-border:        @state-success-border;\r\n\r\n@alert-info-bg:               @state-info-bg;\r\n@alert-info-text:             @state-info-text;\r\n@alert-info-border:           @state-info-border;\r\n\r\n@alert-warning-bg:            @state-warning-bg;\r\n@alert-warning-text:          @state-warning-text;\r\n@alert-warning-border:        @state-warning-border;\r\n\r\n@alert-danger-bg:             @state-danger-bg;\r\n@alert-danger-text:           @state-danger-text;\r\n@alert-danger-border:         @state-danger-border;\r\n\r\n\r\n//== Progress bars\r\n//\r\n//##\r\n\r\n//** Background color of the whole progress component\r\n@progress-bg:                 #f5f5f5;\r\n//** Progress bar text color\r\n@progress-bar-color:          #fff;\r\n//** Variable for setting rounded corners on progress bar.\r\n@progress-border-radius:      @border-radius-base;\r\n\r\n//** Default progress bar color\r\n@progress-bar-bg:             @brand-primary;\r\n//** Success progress bar color\r\n@progress-bar-success-bg:     @brand-success;\r\n//** Warning progress bar color\r\n@progress-bar-warning-bg:     @brand-warning;\r\n//** Danger progress bar color\r\n@progress-bar-danger-bg:      @brand-danger;\r\n//** Info progress bar color\r\n@progress-bar-info-bg:        @brand-info;\r\n\r\n\r\n//== List group\r\n//\r\n//##\r\n\r\n//** Background color on `.list-group-item`\r\n@list-group-bg:                 #fff;\r\n//** `.list-gr