INSERT INTO `app_hrzg_widget_template` VALUES (3,'Header Image','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Header Image Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"parallax\": {\n            \"title\": \"Parallax ON\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\"\n        },\n        \"subline\": {\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"subline\"\n        },\n        \"headline\": {\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"headline\"\n        },\n        \"imageSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Image URL\",\n            \"default\": \"\"\n        },\n        \"patternSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Pattern URL\",\n            \"default\": \"\"\n        }\n    }\n}','<div class=\"header-image text-center text-on-picture{% if parallax == true %} header-fixed{% endif %}\" style=\"background-image: url(/filefly/api?action=stream&path={{ imageSource }}); width: 100%;\">\r\n    <div class=\"pattern\" style=\"background: url(/filefly/api?action=stream&path={{ patternSource }});\">\r\n        <div class=\"container\">\r\n            <h5> {{ subline }} </h5>\r\n            <h1> {{ headline }} </h1>\r\n        </div>    \r\n    </div>\r\n</div>',NULL,NULL);
INSERT INTO `app_hrzg_widget_template` VALUES (4,'Content','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Content with Button Widget\",\n    \"type\": \"object\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"propertyOrder\": 10,\n            \"type\": \"string\",\n            \"title\": \"Skin style\",\n            \"enum\": [\n                \"white\",\n                \"dark\"\n            ]\n        },\n        \"text\": {\n            \"propertyOrder\": 20,\n            \"type\": \"string\",\n            \"title\": \"Text style\",\n            \"enum\": [\n                \"text-left\",\n                \"text-center\",\n                \"text-right\"\n            ]\n        },\n        \"ckbox\": {\n            \"propertyOrder\": 30,\n            \"title\": \"Container on\\/off\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\",\n            \"default\": true\n        },\n        \"subline\": {\n            \"propertyOrder\": 40,\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante.\"\n        },\n        \"headline\": {\n            \"propertyOrder\": 50,\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"Senectus et netus et malesuada fames ac turpis\"\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"format\": \"tabs\",\n            \"title\": \"Text\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Paragraph\",\n                \"properties\": {\n                    \"text_html\": {\n                        \"propertyOrder\": 100,\n                        \"type\": \"string\",\n                        \"title\": \"Text\",\n                        \"default\": \"Pellentesque habitant morbi tristique senectus 