INSERT INTO `app_hrzg_widget_template` VALUES (17,'Header Video','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Header Video Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"subline\": {\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"Subline\"\n        },\n        \"headline\": {\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"Headline\"\n        },\n        \"youtube_id\": {\n            \"type\": \"string\",\n            \"title\": \"YouTube ID\",\n            \"default\": \"opcRx5fn8N8\"\n        },\n        \"imageSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Image URL\",\n            \"default\": \"\"\n        },\n        \"patternSource\": {\n            \"type\": \"string\",\n            \"format\": \"filefly\",\n            \"title\": \"Pattern URL\",\n            \"default\": \"\"\n        }\n    }\n}','<div class=\"header-video hidden-sm hidden-xs\">\r\n    <div style=\"position: relative; max-height: 100%; overflow: hidden; \">\r\n        <div class=\"embed-responsive embed-responsive-16by9\">\r\n            <div id=\"background\">\r\n               <iframe id=\'player\' width=\"100%\" height=\"100%\" src=\"https://www.youtube.com/embed/{{ youtube_id }}?rel=0&amp;controls=0&amp;showinfo=0&amp;autoplay=1&amp;loop=1&amp;playlist={{ youtube_id }}&amp;enablejsapi=1&version=3\" frameborder=\"0\"></iframe>\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"video-front text-on-picture\">\r\n            <h5>{{ subline }}</h5>\r\n            <h1>{{ headline }}</h1>\r\n        </div>\r\n        \r\n        <div class=\"video-overlay\" style=\"background: url(/filefly/api?action=stream&path={{ patternSource }});\"></div>\r\n    </div>\r\n</div>\r\n<div class=\"header-image text-center text-on-picture hidden-lg hidden-md\" style=\"background-image: 