INSERT INTO `app_hrzg_widget_template` VALUES (22,'Content blocks (1-6)','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Content blocks Widget\",\n    \"type\": \"object\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"cols\": {\n            \"propertyOrder\": 10,\n            \"type\": \"string\",\n            \"title\": \"Blocks per row\",\n            \"description\": \"Set the max. amount of cards per row.\",\n            \"enum\": [\n                \"1\",\n                \"2\",\n                \"3\",\n                \"4\",\n                \"6\"\n            ],\n            \"default\": \"1\"\n        },\n        \"skin\": {\n            \"propertyOrder\": 20,\n            \"type\": \"string\",\n            \"title\": \"Skin Style\",\n            \"enum\": [\n                \"white\",\n                \"dark\"\n            ]\n        },\n        \"text\": {\n            \"propertyOrder\": 30,\n            \"type\": \"string\",\n            \"title\": \"Text Style\",\n            \"enum\": [\n                \"text-center\",\n                \"text-left\"\n            ]\n        },\n        \"ckbox\": {\n            \"propertyOrder\": 40,\n            \"title\": \"Container on\\/off\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\",\n            \"default\": true\n        },\n        \"subline\": {\n            \"propertyOrder\": 50,\n            \"type\": \"string\",\n            \"title\": \"Subline\",\n            \"default\": \"Subline\"\n        },\n        \"headline\": {\n            \"propertyOrder\": 60,\n            \"type\": \"string\",\n            \"title\": \"Headline\",\n            \"default\": \"Headline\"\n        },\n        \"blocks\": {\n            \"type\": \"array\",\n            \"title\": \"Contentblock\",\n            \"format\": \"tabs\",\n            \"items\": {\n                \"type\": \"object\",\n                \"title\": \"Contentblock\",\n         