              }\n                }\n            }\n        }\n    }\n}','<div class=\"widget-content blocks bg-{{ skin }} {{ text }}\">\r\n    {% if ckbox == true %}\r\n    <div class=\"container\">\r\n    {% else %}\r\n    <div class=\"no-container\">\r\n    {% endif %}\r\n    \r\n        {% if (subline) != \"\" %}\r\n            <h3>{{ subline }}</h35>\r\n        {% endif %}\r\n            \r\n        {% if (headline) != \"\" %}\r\n            <h2>{{ headline }}</h2>\r\n        {% endif %}\r\n            \r\n        <div class=\"row\">\r\n        {% for block in  blocks %}\r\n            {% if cols == \"6\" %}\r\n            <div class=\"col-xs-4 col-sm-2 col-md-2 col-lg-2\">\r\n            {% elseif cols == \"4\" %}\r\n            <div class=\"col-xs-12 col-sm-6 col-md-3 col-lg-3\">\r\n            {% elseif cols == \"3\" %}\r\n            <div class=\"col-xs-12 col-lg-4\">\r\n            {% elseif cols == \"2\" %}\r\n            <div class=\"col-xs-12 col-md-6\">\r\n            {% else %} \r\n            <div class=\"col-lg-12\">\r\n            {% endif %}\r\n            \r\n                {% if (block.image_url) != \"\" %}\r\n                    <div class=\"img-responsive\">\r\n                         <img src=\"/filefly/api?action=stream&path={{ block.image_url }}\" class=\"img-responsive\"/>\r\n                    </div>\r\n                {% endif %}    \r\n                \r\n                {% if (block.headline_block) != \"\" %}\r\n                    <div class=\"blocks-headline\">\r\n                        <h3>{{ block.headline_block}}</h3>\r\n                    </div>\r\n                {% endif %}\r\n                \r\n                {% if (block.text_html) != \"\" %}\r\n                   {{ block.text_html|raw }}\r\n                {% endif %}\r\n                \r\n                {% if (block.button_text) != \"\" %}\r\n                        <p>\r\n                            {% if block.link_target == true %}\r\n                                <a href=\"{{block.button_url}}\" target=\"_blank\" class=\"btn btn-primary btn-{{ skin }}\">{{block.button_text}}</a>\r\n                            {% else %}\r\n                                <a href=\"{{block.button_url}}\" target=\"_self\" class=\"btn btn-primary btn-{{ skin }}\">{{block.button_text}}</a>\r\n                            {% endif %} \r\n                        </p>\r\n                {% endif %}\r\n            \r\n            </div>\r\n            \r\n        {% endfor %}\r\n        \r\n        </div>\r\n    </div>\r\n</div>\r\n',NULL,'2017-06-19 13:32:03');
INSERT INTO `app_hrzg_widget_template` VALUES (24,'Content & Image','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Content & Image Widget\",\n    \"type\": \"object\",\n    \"required\": [\n        \"skin\"\n    ],\n    \"properties\": {\n        \"skin\": {\n            \"type\": \"string\",\n            \"title\": \"Skin style\",\n            \"enum\": [\n                \"white\",\n                \"dark\"\n            ]\n        },\n        \"text\": {\n            \"type\": \"string\",\n            \"title\": \"Text style\",\n            \"enum\": [\n                \"text-left\",\n                \"text-center\",\n                \"text-right\"\n            ]\n        },\n        \"position\": {\n            \"title\": \"Text position\",\n            \"type\": \"string\",\n            \"enum\": [\n                \"text-on-left\",\n                \"text-on-right\"\n            ]\n        },\n        \"ckbox\": {\n            \"title\": \"Container on\\/off\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\",\n            \"default\": true\n        },\n        \"shadow\": {\n            \"title\": \"image-shadow\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\",\n            \"default\": false\n        },\n        \"text_html\": {\n            \"type\": \"string\",\n            \"title\": \"Text\",\n            \"default\": \"<p>Pellentesque habitant morbi tristique senectus et netus et malesuada<\\/p>\",\n            \"format\": \"html\",\n            \"options\": {\n                \"wysiwyg\": true\n            }\n        },\n        \"button_text\": {\n            \"type\": \"string\",\n            \"title\": \"Button Text\"\n        },\n        \"button_url\": {\n            \"type\": \"string\",\n            \"title\": \"Link \\/ Mailto \\/ Anchor\",\n            \"description\": \"Link: http:\\/\\/www.example.com \\/ Mailto: mailto:mail@info.com \\/ Anchor: #widget-NameID_de\"\n        },\n        \"image_url\": {\n            \"type\": \"string\",\n            \"title\": \"Image URL\",\n            \"format\": \"filefly\",\n            \"default\": \"\\/_phd\\/test.png\"\n        },\n        \"image_seo\": {\n            \"type\": \"string\",\n            \"title\": \"Image SEO\",\n            \"default\": \"Vestibulum tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante.\"\n        }\n    }\n}','<div class=\"widget-content content bg-{{ skin }} {{ text }}\">\r\n    {% if ckbox == true %}\r\n    <div class=\"container\">\r\n    {% else %}\r\n    <div class=\"no-container\">\r\n    {% endif %}\r\n    \r\n        {% if position == \"text-on-left\" %}\r\n        <div class=\"row\">\r\n            \r\n            <div class=\"col-md-6 text-next-image\">\r\n                \r\n                {% if (text_html) != \"\" %}\r\n                    {{ text_html|raw }}\r\n                {% endif %} \r\n                \r\n                {% if (button_text) != \"\" %}\r\n                    <a href=\"{{ button_url }}\" class=\"btn btn-primary btn-{{ skin }}\">{{ button_text }}</a>\r\n                {% endif %}  \r\n                \r\n            </div>   \r\n            \r\n            <div class=\"col-md-6 {% if shadow == true %}shadow-image small-image{% endif %}\">\r\n                <img class=\"img-responsive\"\r\n                            src=\"/filefly/api?action=stream&path={{ image_url }}\"\r\n                            alt=\"{{ image_seo }}\">\r\n            </div>\r\n            \r\n         </div>\r\n        {% endif %}\r\n        \r\n        {% if position == \"text-on-right\" %}\r\n        <div class=\"row\">\r\n            \r\n            <div class=\"col-md-6 {% if shadow == true %}shadow-image small-image{% endif %}\">\r\n                \r\n                <img class=\"img-responsive\"\r\n                            src=\"/filefly/api?action=stream&path={{ image_url }}\"\r\n                            alt=\"{{ image_seo }}\">\r\n            </div>\r\n            \r\n            <div class=\"col-md-6 text-next-image\">\r\n                {% if (text_html) != \"\" %}\r\n                    {{ text_html|raw }}\r\n                {% endif %}\r\n                \r\n                {% if (button_text) != \"\" %}\r\n                    <a href=\"{{ button_url }}\" class=\"btn btn-primary btn-{{ skin }}\">{{ button_text }}</a>\r\n                {% endif %} \r\n                \r\n            </div> \r\n            \r\n        </div>\r\n        {% endif %}\r\n        \r\n    </div>\r\n</div>\r\n\r\n',NULL,'2017-06-19 15:22:27');
INSERT INTO `app_hrzg_widget_template` VALUES (26,'Big Image','hrzg\\widget\\widgets\\TwigTemplate','{\n    \"title\": \"Big Image Widget\",\n    \"type\": \"object\",\n    \"properties\": {\n        \"skin\": {\n            \"type\": \"string\",\n            \"title\": \"Widget style\",\n            \"enum\": [\n                \"white \",\n                \"gradient\"\n            ]\n        },\n        \"contained\": {\n            \"title\": \"Container ON\",\n            \"description\": \"Check if container is needed, without container = parallax\",\n            \"type\": \"boolean\",\n            \"format\": \"checkbox\"\n        },\n        \"image_url\": {\n            \"type\": \"string\",\n            \"title\": \"Image URL\",\n            \"format\": \"filefly\",\n            \"default\": \"\\/example\\/example4.jpg\"\n        },\n        \"image_seo\": {\n            \"type\": \"string\",\n            \"title\": \"Image SEO\",\n            \"default\": \"Vestibulum tortor quam.\"\n        }\n    }\n}','<section class=\"bg-{{ skin }}\">\r\n    \r\n     {% if contained != \"true\" %}\r\n    \r\n        <figure class=\"separator-image\">\r\n            <div id=\"parallax\" style=\"background-image:url(/filefly/api?action=stream&path={{ image_url }})\" alt=\"{{ image_seo }}\"></div> \r\n        </figure>\r\n                \r\n    {% else %}\r\n    \r\n    \r\n        <div class=\"container\">\r\n            <div class=\"row\">\r\n                <figure class=\" col-xs-12 col-md-12\">\r\n    \r\n                        <img class=\"img-responsive big-image\"\r\n                            src=\"/filefly/api?action=stream&path={{ image_url }}\"\r\n                            alt=\"{{ image_seo }}\">\r\n                </figure>\r\n            </div>\r\n        </div>\r\n\r\n    {% endif %}\r\n    \r\n</section>',NULL,'2017-06-19 15:21:28');
