INSERT INTO `app_language` VALUES ('zh-TW','zh','tw','â°ââ ÃÃ±Ã¡(ÃÃ¨âÃÃINSERT INTO `app_less` VALUES (14,'doro-main','// Main LESS file\r\n\r\n// Theme\r\n// see http://bootswatch.com\r\n//\r\n// available themes: \r\n// cerulean,cosmo,cyborg,darkly,flatly,journal,lumen,paper,readable,\r\n// sandstone,simplex,slate,spacelab,superhero,united,yeti\r\n@theme: \'cosmo\';\r\n\r\n// Framework and base theme\r\n@import \"/app/vendor/bower/bootstrap/less/bootstrap.less\";\r\n@import \"/app/vendor/thomaspark/bootswatch/@{theme}/variables.less\";\r\n@import \"/app/vendor/thomaspark/bootswatch/@{theme}/bootswatch.less\";\r\n@import \"//code.ionicframework.com/ionicons/2.0.1/css/ionicons.min.css\";\r\n\r\n\r\n// Theme customizations\r\n\r\n// Style for widget templates\r\n@import \"doro-widgets\";\r\n@import \"forms\";\r\n\r\n// Fonts //\r\n\r\n@import url(https://fonts.googleapis.com/css?family=Source+Sans+Pro:300,400);\r\n@import url(https://fonts.googleapis.com/css?family=Roboto:100,300,400,500,700);\r\n\r\n@font-family-sans-serif: \'Source Sans Pro\', \'sans-serif\';\r\n@font-family-serif: \'Roboto\', \'sans-serif\';\r\n\r\n@icon-font-path: \'//maxcdn.bootstrapcdn.com/bootstrap/3.3.7/fonts/\';\r\n\r\n\r\n// Colors //\r\n\r\n@brand-primary: #1abc9c; \r\n@brand-second: #547077; \r\n@brand-intens: #e46666;\r\n@brand-dark: #333333;\r\n\r\n\r\n// buttons //\r\n\r\n// extra class for buttons to avoid widget control buttons to look strange //\r\n\r\n.btn-white {\r\n    margin: 40px 0 0 0;\r\n    font-family: @font-family-sans-serif;\r\n    font-weight: 300;\r\n    font-size: 18px;\r\n    line-height: 1.5;\r\n    letter-spacing: 0.05em;\r\n    padding: 14px;\r\n    min-width: 200px;\r\n    border: 0;\r\n}\r\n\r\n.btn-primary:hover {\r\n    color: white;\r\n    background-color: @brand-second;\r\n    border-color: @brand-second;\r\n}\r\n\r\n.btn-dark {\r\n    margin: 40px 0 0 0;\r\n    font-family: @font-family-sans-serif;\r\n    font-weight: 300;\r\n    color: @brand-primary;\r\n    background-color: white;\r\n    font-size: 18px;\r\n    line-height: 1.5;\r\n    letter-spacing: 0.05em;\r\n    padding: 14px;\r\n    min-width: 200px;\r\n    border: 0;\r\n}\r\n\r\n.btn-dark:hover {\r\n    color: @brand-primary;\r\n    background-color: white;\r\n    opacity: 0.8;\r\n}\r\n\r\n// healdines //\r\n\r\nh1 {\r\n    font-size: 50px;\r\n    font-family: @font-family-serif;\r\n    font-weight: 300;\r\n    text-transform: uppercase;\r\n    //letter-spacing: 0.05em;\r\n    @media (max-width: @screen-xs-max) {\r\n        font-size: 25px;\r\n        margin-bottom: 0px;\r\n        }\r\n}\r\n\r\nh2 {\r\n    font-size: 40px;\r\n    font-family: @font-family-serif;\r\n    font-weight: 300;\r\n    letter-spacing: 0.05em;\r\n    margin-bottom: 50px;\r\n    @media (max-width: @screen-xs-max) {\r\n        font-size: 30px;\r\n        margin-bottom: 15px;\r\n        }\r\n}\r\n\r\nh3, h4, h5, h6 {\r\n    line-height: 1.4;\r\n    letter-spacing: 0.05em;\r\n} \r\n\r\nh3 {\r\n    font-size: 18px;\r\n    font-family: @font-family-sans-serif;\r\n    font-weight: 300;\r\n    text-transform: uppercase;\r\n}\r\n\r\nh4 {\r\n    font-size: 40px;\r\n    font-family: @font-family-sans-serif;\r\n    font-weight: 400;\r\n    text-transform: uppercase;\r\n}\r\n\r\nh5 {\r\n    font-size: 15px;\r\n    font-family: @font-family-sans-serif;\r\n    font-weight: 400;\r\n    text-transform: uppercase;\r\n    margin-bottom: 20px;\r\n}\r\n\r\np {\r\n    font-size: 18px;\r\n    letter-spacing: 0.01em;\r\n    font-weight: 100;\r\n    line-height: 1.7;\r\n    font-family: @font-family-sans-serif;\r\n}\r\n\r\nul {\r\n    font-size: 16px;\r\n    letter-spacing: 0.01em;\r\n    font-weight: 300;\r\n    line-height: 1.6;\r\n    font-family: @font-family-sans-serif;\r\n}\r\n\r\n\r\n// Backgrounds //\r\n\r\n\r\n.bg-white {\r\n    background: #FFF;\r\n}\r\n.bg-dark {\r\n    color: white;\r\n    height: 100%;\r\n    background: @brand-second;\r\n    .btn-dark {\r\n        color: @brand-second;\r\n    }\r\n    a {\r\n        color: white;\r\n    }\r\n}\r\n\r\n// LINKS & HOVER //\r\n \r\na  {\r\n    color: @brand-primary;\r\n    text-decoration: none;\r\n        & table a:not(.btn), .table a:not(.btn) {\r\n        text-decoration: none;\r\n        }\r\n}\r\n\r\na:hover, a:active,  a:focus, a.list-inline, .navbar-default .navbar-brand:hover .navbar-default .navbar-brand:focus {\r\n    color: @brand-primary;\r\n    text-decoration: none;\r\n}\r\n\r\n// Navbar //\r\n\r\n@navbar-border-radius: 0px;\r\n@navbar-margin-bottom: 0px;\r\n@border-radius-base: 0;\r\n\r\n\r\n// Navbar //\r\n\r\n\r\n.navbar {\r\n    font-weight: 400;\r\n    text-decoration: none;\r\n    a {\r\n        text-transform: uppercase;\r\n        font-family: @font-family-sans-serif;\r\n        letter-spacing: 0.25em;\r\n        font-weight: 400;\r\n        color: white;\r\n        font-size: 12px;\r\n    }\r\n    .navbar-nav {\r\n        display: inline-block;\r\n        float: none;\r\n        vertical-align: top;\r\n    }\r\n    .navbar-collapse {\r\n        text-align: center;\r\n    }\r\n}\r\n\r\n.navbar-default .navbar-nav > li > a {\r\n    color: white;\r\n}\r\n\r\n.navbar-default .navbar-toggle {\r\n    background-color: rgba(0, 0, 0, 0.9);\r\n}\r\n\r\n\r\nnav.navbar-default .navbar-collapse, nav.navbar-default .navbar-default .navbar-form {\r\n    background-color: transparent;\r\n    @media (max-width: @screen-xs-max) {\r\n        background-color: rgba(0, 0, 0, 0.9);\r\n    }\r\n}\r\n\r\nnav.navbar-default {\r\n    border: 0px;\r\n    background-color: rgba(0, 0, 0, 0.6);\r\n    @media (max-width: @screen-xs-max) {\r\n        background-color: transparent;\r\n    }\r\n    .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {\r\n    color: @brand-primary;\r\n    font-weight: 400;\r\n    background-color: transparent;\r\n    }\r\n    .navbar-collapse {\r\n        .navbar-logo {\r\n            height: 25px;\r\n      